INSERT INTO web_pruvodcestudenta_be.sections (id, title, description, created_at, updated_at, slug, color, visible, icon) VALUES (2, 'Volný čas', 'Trávit čas jen ve škole a v knihovně není pro každého. Pokud chceš podniknout i něco dalšího, v této sekci narazíš na různé možnosti, co můžeš ve Zlíně podniknout.', null, '2023-07-26 05:07:22', 'zivot-ve-zline', '#fda6a4', 1, 'https://pruvodcestudenta.utb.cz/backend/public/icons/volny-cas.svg');
INSERT INTO web_pruvodcestudenta_be.sections (id, title, description, created_at, updated_at, slug, color, visible, icon) VALUES (3, 'Studentské organizace', '<p>Škola je pro Tebe málo a chceš být součástí i něčeho dalšího? Tady najdeš veškeré studentské organizace, které ve Zlíně působí a kterých můžeš být součástí.</p>', null, '2023-10-01 18:21:22', 'studentske-organizace', '#ffe793', 1, 'https://pruvodcestudenta.utb.cz/backend/public/icons/studentske-organizace.svg');
INSERT INTO web_pruvodcestudenta_be.sections (id, title, description, created_at, updated_at, slug, color, visible, icon) VALUES (4, 'Kam na jídlo, kávu či pivo', '<p>Všemi milovaná menza bohužel nemá otevřeno neustále a tak Ti tady přinášíme výběr podniků, které stojí za to navštívit.</p>', null, '2023-10-01 18:21:41', 'kam-na-jidlo', '#e9c6ff', 1, 'https://pruvodcestudenta.utb.cz/backend/public/icons/jidlo-kava-pivo.svg');
INSERT INTO web_pruvodcestudenta_be.sections (id, title, description, created_at, updated_at, slug, color, visible, icon) VALUES (5, 'Praktické rady', '<p>Zde jsme pro tebe nachystali další užitečné informace do začátku studia</p>', null, '2023-10-05 15:37:45', 'prakticke-rady', '#ffe4f3', 1, 'https://pruvodcestudenta.utb.cz/backend/public/icons/prakticke-rady.svg');
INSERT INTO web_pruvodcestudenta_be.sections (id, title, description, created_at, updated_at, slug, color, visible, icon) VALUES (6, 'Život na univerzitě', '<p>Jako novopečený vysokoškolák by ses měl seznámit s&nbsp;tím, kdo univerzitu řídí, jak funguje a co vše je součástí univerzity</p>', null, '2023-10-05 15:34:58', 'zivot-na-univerzite', '#acf0f9', 1, 'https://pruvodcestudenta.utb.cz/backend/public/icons/zivot-na-univerzite.svg');
INSERT INTO web_pruvodcestudenta_be.sections (id, title, description, created_at, updated_at, slug, color, visible, icon) VALUES (7, 'Akademické poradny', 'Univerzita není jen vzdělávací zařízení. Pokud se během studia dostaneš do problémů, univerzita nabízí pro studenty několik poraden, kde ti pomůžou tvůj problém vyřešit.', null, '2023-10-05 15:34:43', 'akademicke-poradny', '#bdf7b4', 1, 'https://pruvodcestudenta.utb.cz/backend/public/icons/akademicke-poradny.svg');
INSERT INTO web_pruvodcestudenta_be.sections (id, title, description, created_at, updated_at, slug, color, visible, icon) VALUES (8, 'Studium', '<p>Studium je to, proč jsi tady. Se studiem je spojeno několik pojmů, pravidel a právě zde jsme pro Tebe nachystali přehled toho nejdůležitějšího</p>', null, '2023-10-05 15:38:22', 'studium', '#f7567c', 1, 'https://pruvodcestudenta.utb.cz/backend/public/icons/studium.svg');
