INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (12, 'Zelenáčova Šopa', '<p>dsadasda</p>', 4, 1, null, '2024-06-09 14:22:26', 'zelenacova-sopa', '#e9c6ff', 'https://www.sopa.cz/', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (50, 'Restaurace U Ševce', 'Restaurace v centru. Pokud máš chuť na Českou klasiku tak jsi tady správně. Ale mají zde i jiná jídla. To ale neznamená, že si sem nemůžeš zajít jen tak na pivo. Poblíž Koleje U7. Mají dokonce i možnost objednání dovozu.', 4, 1, '2023-06-19 10:26:26', '2023-06-19 10:26:26', 'restaurace-u-sevce', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (57, 'jedním tahem', 'Džungle, alternativní, galerie, kavárna
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (58, 'Velkokavárna Gulliver', 'Jsme atmosférický café bar a múzický klub s širokou nabídkou kvalitních alko i nealko nápojů, čajů, sladkých pokušení a pochutin jak od prémiových výrobců, tak i z vlastní produkce. Hlavní specialitou jsou naše vlastní originální sodovky-limonády SYFOTEUS, které luhujeme ze směsí bylin, čajů a sušeného ovoce. V nabídce máme také 11 druhů toustů vlastních receptur pro masomilce, vegetariány i vegany.', 4, 1, '2023-07-06 17:15:54', '2023-07-06 17:15:54', 'velkokavarna-gulliver', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (59, 'Hasičárna Novesta', 'Industriální a chlapský duch  hasičských zbrojnic umocní návštěvu vynikajícími burgery nebo pizzou a skvěle ošetřeným pivem.', 4, 1, '2023-07-06 17:19:16', '2023-07-06 17:19:16', 'hasicarna-novesta', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (60, 'Laser Game Zlín', 'Scifi prostředí arény, hi-tech vybavení a akční zábava pro tebe a tvou partu. To je Laser Game Zlín.', 2, 1, '2023-07-06 17:21:57', '2023-07-06 17:21:57', 'laser-game-zlin', '#fda6a4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (61, 'Steak House Legenda', 'Restaurace navazuje na více jak 20 letou tradici Irské restaurace na Interhotelu Zlín**** ve Zlíně. Naši kuchyni vévodí steaky z mas té nejvyšší kvality, doplněné tradičním Plzeňským pivem a Irským pivem Guinnes.', 4, 1, '2023-07-06 17:25:48', '2023-07-06 17:25:48', 'steak-house-legenda', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (62, 'Bistrotéka Valachy', 'Bistrotéka Valachy propojuje pod jednou střechou několik gastro provozů a jejím srdcem je celodenní bistro s nabídkou snídaní, obědů a večeří. Kromě výborného jídla si u nás můžete dopřát i gastronomický zážitek například v podobě kurzů ve Škole vaření.', 4, 1, '2023-07-06 17:28:03', '2023-07-06 17:28:03', 'bistroteka-valachy', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (63, 'Bar 1931', 'V Baru 1931 vám rádi namíchají drink pro daný okamžik. Najdete zde bohatou nabídku destilátů, vlastní kreativní koktejly, kvalitní vína, na 80 druhů rumů, gin&tonic festival menu, ale také rumové, ginové či whisky degustace pod vedením profesionálů. Vychutnat si můžete také nabídku poledního menu či sezónního menu.', 4, 1, '2023-07-06 17:30:40', '2023-07-06 17:30:40', 'bar-1931', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (64, 'Budvarka Zlín', 'BUDVARKA  je  značkovou restaurací českého prémiového piva Budweiser Budvar. V BUDVARCE se propojuje moderní a klasický styl.', 4, 1, '2023-07-06 17:32:08', '2023-07-06 17:32:08', 'budvarka-zlin', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (65, 'Phởlévkárna', 'Phởlévkárna je samoobslužný bufet ve Zlíně. Najdete v ní tradiční vietnamská jídla.', 4, 1, '2023-07-09 15:20:53', '2023-07-09 15:20:53', 'pholevkarna', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (66, 'Faency Fries', 'FÆNCY FRIES je mladý český street food koncept. Naším povoláním je vyrábět skvělé hranolky a domácí dipy z čerstvých a lokálních surovin nejvyšší kvality.', 4, 1, '2023-07-09 15:23:29', '2023-07-09 15:23:29', 'faency-fries', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (67, 'PUOR Zlín', 'PUOR je koncept Plzeňského Prazdroje a představuje plzeňský ležák v prostředí, které odpovídá české pivní kultuře.
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (68, 'JAMAI CAFE', 'Kavárna 21 ve Zlíně se nachází v Baťově mrakodrapu na střeše v 16. patře, a je oblíbeným místem pro setkání s přáteli, pracovní schůzky nebo jen pro odpočinek s výhledem na centrum města Zlín.', 4, 1, '2023-07-09 15:26:25', '2023-07-09 15:26:25', 'jamai-cafe', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (69, 'KAVÁRNA TOVÁRNA', 'Unikátní místo, kde se propojuje kavárna s kulturním multifunkčním prostorem v industriálním stylu bývalého baťovského areálu.', 4, 1, '2023-07-09 15:30:01', '2023-07-09 15:30:01', 'kavarna-tovarna', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (70, 'Baťův mrakodrap 21', 'Funkcionalistická budova mrakodrapu byla postavena v letech 1936 až 1938 jako sídlo firmy Baťa. Budova se 17 patry dosahuje výšky 77,5 m. Dnes v budově sídlí krajský a finanční úřad, restaurace a jsou zde i prostory pro výstavy. V nejvyšším patře se nachází kavárna a vyhlídková plošina.', 2, 1, '2023-07-09 15:31:12', '2023-07-09 15:31:12', 'batuv-mrakodrap-21', '#fda6a4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (71, 'Star club Flip', 'Jediný taneční klub ve Zlíně, kde jsou pravidelně pořádané akce, pro více info sleduj jejich Instagram', 2, 1, '2023-07-09 15:36:26', '2023-07-09 15:36:26', 'star-club-flip', '#fda6a4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (72, 'CANADA PUB', 'Pivnice v centru Zlína, kde najdeš nejen tankového pivo', 4, 1, '2023-07-09 15:39:10', '2023-07-09 15:39:10', 'canada-pub', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (73, '14l15 Baťův institut', '14|15 BAŤŮV INSTITUT ve Zlíně vznikl konverzí dvou továrních budov bývalého Baťova továrního areálu. Dříve industriální budovy se změnily v moderní kulturní centrum, které se řadí svým konceptem a expozicemi k evropským unikátům. Ve 14|15 BAŤOVĚ INSTITUTU sídlí Krajská galerie výtvarného umění ve Zlíně, Muzeum jihovýchodní Moravy ve Zlíně a Krajská knihovna Františka Bartoše ve Zlíně.
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (74, 'Muzeum jihovýchodní Moravy ve Zlíně', 'Muzeum jihovýchodní Moravy ve Zlíně se nachází ve 14. budově baťovského areálu. Najdete zde expozici Františka Bartoše či Princip Baťa – Dnes fantazie, zítra skutečnost. Muzeum se zaměřujeme na témata Obuv a produkce firmy Baťa, Film a Cestovatelství. K dispozici je edukační místnost, badatelna a knihovna.', 2, 1, '2023-07-09 15:51:11', '2023-07-09 15:51:11', 'muzeum-jihovychodni-moravy-ve-zline', '#fda6a4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (75, 'Krajská galerie výtvarného umění ve Zlíně', 'Galerie se věnuje především českému a slovenskému výtvarnému umění a architektuře 19. až 21. století. Spravuje a dále buduje sbírku uměleckých děl, pořádá výstavy, vzdělávací programy a spektrum své činnosti doplňuje o akce propojující různé obory kultury. Stimulace výtvarného života v regionu.', 2, 1, '2023-07-09 15:56:20', '2023-07-09 15:56:20', 'krajska-galerie-vytvarneho-umeni-ve-zline', '#fda6a4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (76, 'Zámek Zlín', 'Čtyřkřídlý renesanční zámek vznikl přestavbou původní tvrze ve 2. polovině 16. století. Kolem roku 1780 byl zámek upraven v duchu baroka, poslední úpravy proběhly kolem r. 1905. Od roku 1929 je objekt v majetku města, které v jeho prostorech do roku 2012 provozovalo Muzeum jihovýchodní Moravy a Krajskou galerii výtvarného umění.
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (77, 'Vegetariánská restaurace Pranaya', 'Pranaya je samoobslužná restaurace, jídla jsou podávána formou bufetu. Na oběd tedy nemusíte vůbec čekat, stačí si jen naložit a zaplatit u pokladny. Také je možné si odnést jídlo s sebou. Restaurace nabízí příjemný a světlý interiér se 46 místy k sezení, moderní vybavení, nekuřácké prostředí a bezbariérový vstup.', 4, 1, '2023-07-09 16:02:03', '2023-07-09 16:02:03', 'vegetarianska-restaurace-pranaya', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (78, 'Poe-Poe', 'Restaurace představují nové rychlé občerstvení, jehož podstatou jsou čerstvé suroviny. Z nich přímo před zákazníky připravujeme slané i sladké pochoutky, vynikající kávu a další nápoje za opravdu skvělé ceny. Vítáme i zákazníky bez rezervace, skupiny a děti. Nachystáme vám také jídlo s sebou.', 4, 1, '2023-07-09 16:08:44', '2023-07-09 16:08:44', 'poe-poe', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (79, 'Kozlovna Celnice', 'Provozujeme restauraci s nabídkou tradiční české kuchyně a denního menu. Čepujeme pivo Kozel 11°. K dispozici je Wi-Fi připojení. Prostory restaurace jsou klimatizované. Součástí je také letní zahrádka.', 4, 1, '2023-07-09 16:09:44', '2023-07-09 16:09:44', 'kozlovna-celnice', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (80, 'Kafec Zlínský', 'Kavárna s výběrovou kávou, pravými belgickými waflemi a bohatými snídaněmi.
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (81, 'Kafe na rohu', 'Moderní kafe bistro zaměřené na prodej nápojů a občerstvení s sebou. Stylová kavárna v centru Zlína. Nabízíme nejen kávové nápoje, ale také čerstvé svačinky a dezerty z naší vlastní cukrářské výroby.', 4, 1, '2023-07-09 16:15:15', '2023-07-09 16:15:15', 'kafe-na-rohu', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (82, 'Leaves Lounge', 'Lounge bar v centru Zlína
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (83, 'Café Bistro Park', 'Cafe bistro s naší čerstvě praženou kávou, na mlýnku vždy více odrůd. Najdete u nás nealkoholické i alkoholické drinky, limonády i koktejly, víno, Prosecco Mionetto. Čerstvě upečené pečivo-croissanty, koláčky, dezerty- domácí tiramisu, dorty. Různé druhy panini vždy čerstvé na gril, čerstvé saláty.', 4, 1, '2023-07-09 16:36:22', '2023-07-09 16:36:22', 'cafe-bistro-park', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (84, 'Pštros - art, coffee & music bar', 'BAR-KAVÁRNA-HUDBA-VÝSTAVY-VERNISÁŽE', 4, 1, '2023-07-09 16:39:05', '2023-07-09 16:39:05', 'pstros-bar', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (85, 'Bůrger', 'Nabízíme burgery z hovězího Angus, jehněčí, vegetariánské, bezlepkové, saláty, bruschetty, další speciality a bulky z naší pece denně čerstvé.', 4, 1, '2023-07-09 16:39:48', '2023-07-09 16:39:48', 'burger', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (86, 'Café 204', 'Stylová kavárna, bistro, wine & cocktail bar v parku Komenského ve Zlíně', 4, 1, '2023-07-09 16:40:32', '2023-07-09 16:40:32', 'cafe-204', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (87, 'Mr. Coffee', 'Rodinná pražírna výběrové kávy. Nabízíme skvělou zrnkovou nebo mletou arabiku, kávu s sebou, možnost se u nás posadit a podívat se, jak pražíme. S výběrem a přípravou Vám rádi poradíme.', 4, 1, '2023-07-09 16:41:13', '2023-07-09 16:41:13', 'mr-coffee', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (89, 'Retro', 'Retro je kavárnou s vynikající kávou Pellini a koktejlový bar, který Vám umožní zažít pohodu v uvolněné atmosféře 50. let.', 4, 1, '2023-07-09 16:55:19', '2023-07-18 13:12:53', 'retro', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (90, 'Café Archa', 'Nabízíme snídaně, kávu, dezerty, dorty, alkoholické i nealkoholické nápoje.', 4, 1, '2023-07-09 16:57:00', '2023-07-09 16:57:00', 'cafe-archa', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (91, 'Sklep 33', 'Sklep 33 je music bar/club/kavárna v centru Zlína.', 4, 1, '2023-07-09 16:58:33', '2023-07-09 16:58:33', 'sklep-33', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (92, 'Infinity coffee & cocktail bar', 'Moderní koktejlový bar v centru města. Široká nabídka koktejlů, rumů, ginů a whisky.', 4, 1, '2023-07-09 16:59:25', '2023-07-09 16:59:25', 'infinity-bar', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (93, 'FOG hookah lounge', 'Lounge s nabídkou vodních dýmek, signature drinků a limonád v centru Zlína', 4, 1, '2023-07-09 17:00:48', '2023-07-09 17:00:48', 'fog-hookah-lounge', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (94, 'Multikino Golden Apple Cinema', 'Multikino se šesti kinosály', 2, 1, '2023-07-09 17:02:41', '2023-07-09 17:02:41', 'multikino-golden-apple-cinema', '#fda6a4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (95, 'Městské divadlo Zlín', 'Městské divadlo nabízí inscenace a muzikály,. Vstupenky lze zajistit jednotlivě nebo celoročním zvýhodněným předplatným. Nabízíme výhodné vstupy pro MŠ, ZŠ a SŠ.', 2, 1, '2023-07-09 17:04:20', '2023-07-09 17:04:20', 'mestske-divadlo-zlin', '#fda6a4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (96, 'Terasa 1', 'Oblíbený zlínský podnik, nabízí příjemné posezení nejen u vyhlášené kávy. Přináší také neustále se měnící nabídku obědů a nechybí ani perfektně vyladěné drinky barmanů.', 4, 1, '2023-07-09 17:07:11', '2023-07-09 17:07:11', 'terasa-1', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (97, 'Nový Svět', 'Nový Svět je místo s neotřelou atmosférou v samém srdci Zlína. Setkávají se zde příběhy lidí, kultur, národností i věků.', 4, 1, '2023-07-09 17:08:54', '2023-07-09 17:08:54', 'novy-svet', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (98, 'Pivnice U Máců', 'Pivnice U Máců je nejstarší fungující pivnice ve Zlíně. Pivo zde čepují od roku 1931.', 4, 1, '2023-07-09 17:10:13', '2023-07-09 17:10:13', 'pivnice-u-macu', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (99, 'KAFÖ', 'Malá kavárnička s jednou nejlepších káv ve Zlíně. Na mlýnku naleznete neustále nové chutě a jen s velmi malou pravděpodobností si zde dáte dvakrát tu stejnou kávu. Pro otvírací dobu doporučujeme sledovat jejich Instagram!', 4, 1, '2023-07-18 14:50:43', '2023-07-18 14:50:43', 'kafo', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (100, 'Pizzerie La Strada', 'Pizzerie s mnohaletou tradicí a zkušenostmi. Pizzu děláme ručně dle vlastních receptur. Poskytujeme rozvoz pizzy, těstovin a salátů.', 4, 1, '2023-07-25 09:17:22', '2023-07-25 09:17:22', 'pizzerie-la-strada', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (101, 'Café Malá scéna', 'Mlhavá rána, rušná poledne či večery u západu slunce - Café Malá Scéna je tu pro vás celý den. V útulných a stylových prostorách kavárny si oddychnete od každodenního shonu nebo v klidu vyřešíte důležité schůzky. Kromě výtečné kávy pro vás denně chystáme speciální snídaňové menu, domácí dortíky a limonády nebo čerstvé a zdravé smoothies.', 4, 1, '2023-07-25 09:18:46', '2023-07-25 09:18:46', 'cafe-mala-scena', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (102, 'Radegastovna Rex', 'Vychutnej si skvělý Radegast v Radegastovna REX v lokalitě Zlín spolu s pořádným jídlem a kvalitním tankovým pivem v originálním prostředí!', 4, 1, '2023-07-25 09:20:50', '2023-07-25 09:20:50', 'radegastovna-rex', '#e9c6ff', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (103, 'E-KARTS Zlín', 'E-Karts Zlín je nově otevřená motokárová dráha, která disponuje nejnovějším typem elektrických motokár francouzské značky Sodi a stylovým zázemím včetně baru. Dráha se rozprostírá na ploše 2 000 m čtverečních s vyváženým počtem zatáček a rovinek.', 2, 1, '2023-07-25 09:35:41', '2023-07-25 09:35:41', 'e-karts-zlin', '#fda6a4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (104, 'ZOO Zlín Lešná', 'Nejnavštěvovanější ZOO na Moravě.
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (134, 'Vedení univerzity', '<p>Vedení univerzity tvoří <strong>rektor, prorektoři</strong> (pro tvůrčí činnosti, pro pedagogickou činnost, pro internacionalizaci, pro vnitřní a vnější vztahy a pro rozvoj), &nbsp;a dále <strong>kvestor, kancléř</strong> a <strong>předseda Akademického senátu UTB.</strong></p><p><strong>Akademický senát UTB&nbsp;</strong>je samosprávným zastupitelským orgánem univerzity. Volí rektora, schvaluje vnitřní předpisy univerzity a fakult, dohlíží na finanční záležitosti univerzity a projednává mnoho dalších důležitých záležitostí. Každá fakulta UTB je v&nbsp;něm zastoupena čtyřmi akademickými pracovníky a dvěma studenty, takže má celkem 36 členů.</p><p>Detailní informace o struktuře a orgánech UTB najdeš <a href="https://www.utb.cz/univerzita/o-univerzite/struktura/organy/">zde</a>.&nbsp;</p>', 6, 3, '2023-09-22 12:45:08', '2023-09-22 12:47:08', 'vedeni-univerzity', '#acf0f9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (135, 'Vedení fakulty', '<p>Na fakultě bdí nad veškerým děním <strong>děkan</strong>, kterého volí <strong>akademický senát fakulty</strong> a jeho funkční období je čtyřleté. Potkáš se s ním nejen při akademických obřadech, jako jsou imatrikulace a promoce, ale i v běžném životě. Kromě děkana patří k vedení fakulty i <strong>proděkani</strong>. Zpravidla jde o proděkany pro pedagogickou činnost, tvůrčí činnost, mezinárodní vztahy a pro spolupráci s&nbsp;praxí.</p><p>&nbsp;</p><p>Součástí každé fakulty je <strong>tajemník</strong>, který se stará o hospodaření fakulty.</p><p>&nbsp;</p><p>Podobně jako je celouniverzitní <strong>akademický senát</strong>, každá fakulta má svůj vlastní senát, který také tvoří studenti a akademičtí pracovníci.&nbsp;</p><p>&nbsp;</p><p>Fakulta je členěna na ústavy. Někde se jim říká katedry, v uměleckých oborech zase ateliéry.&nbsp;</p>', 6, 3, '2023-09-22 12:46:55', '2023-09-22 12:46:55', 'vedeni-fakulty', '#acf0f9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (136, 'Univerzitní institut', '<p>Univerzitní institut<strong> </strong>vytváří podmínky pro spolupráci UTB s&nbsp;průmyslovou sférou ve výzkumu, vývoji a inovacích a transferu&nbsp; &nbsp;technologií. Podporuje tvůrčí aktivity zaměstnanců UTB, věnuje se ochraně práv duševního vlastnictví, napomáhá vzniku nových inovačních firem a mnoho dalšího. V&nbsp;jeho rámci lze studovat, po dokončení magisterského studia, v&nbsp;některém z&nbsp;doktorských studijních programů.&nbsp;</p>', 6, 3, '2023-09-22 13:10:48', '2023-09-22 13:10:48', 'univerzitni-institut', '#acf0f9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (137, 'Součásti univerzity', '<p>Mezi součásti univerzity patří: 6 fakult, Univerzitní institut, Knihovna UTB a Koleje a menza.&nbsp;</p>', 6, 3, '2023-09-22 13:12:29', '2023-09-22 13:12:29', 'souasti-univerzity', '#acf0f9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (138, 'Fakulty', '<p>Univerzita Tomáše Bati ve Zlíně se skládá celkem z&nbsp;6 fakult, z&nbsp;nichž 5 fakult sídlí ve Zlíně a jedna v&nbsp;Uherském Hradišti. Ve Zlíně mají sídlo: Fakulta technologická, Fakulta managementu a ekonomiky, Fakulta multimediálních komunikací, Fakulta aplikované informatiky a Fakulta humanitních studií.</p><p>Šestá, nejmladší, Fakulta logistiky a krizového řízení, sídlí v Uherském Hradišti.&nbsp;</p><p>Detaily o studiu a životě na všech fakultách se dozvíš na <a href="http://www.utb.cz">www.utb.cz</a>, v&nbsp;sekci Fakulty a součásti.&nbsp;</p>', 6, 3, '2023-09-22 13:13:17', '2023-09-22 13:13:17', 'fakulty', '#acf0f9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (139, 'Knihovna UTB', '<p>Knihovnu UTB najdeš v&nbsp;moderní prosklené budově na náměstí T. G. Masaryka ve Zlíně, v&nbsp;níž sídlí i rektorát, a má označení U13. Hlavní vstup najdeš ve 2. patře budovy (vyjdeš velké schody přímo naproti vstupu a jsi tam). Dovnitř se dostaneš přes turnikety, které tě pustí na tvůj studentský průkaz.</p><p>&nbsp;</p><p>Otevřeno je od pondělí do čtvrtka (8:00–19:00), v pátek (8:00–18:00) a v sobotu (9:00–14:00).&nbsp;</p><p>&nbsp;</p><p>Co ti knihovna nabízí:</p><ul><li>půjčování knih, skript, časopisů i denního tisku (něco domů, něco pro studium v knihovně)</li><li>přístup k téměř nekonečnému množství elektronických zdrojů a databází (oceníš, až budeš psát bakalářku či diplomku)</li><li>wifi, 200 počítačů, 500 studijních míst</li><li>relaxační místnosti, individuální studovny a dětský koutek</li><li>čarovné a populární čtvrté patro, které ti dá ještě víc prostoru na studium, relax i práci - a má i prodlouženou otevírací dobu, takže tam přes týden můžeš být až do 22:00</li><li>tisk, kopírování, skenování apod.</li><li>vazbu bakalářek a diplomek</li><li>on-line knihovnu Bookport, kde je kromě odborné literatury i hromada české beletrie</li><li>kurzy a konzultace, které se hodí, když píšeš závěrečnou práci.</li></ul><p>&nbsp;</p><p>Pokud si nebudeš něčím jistý/á, a budeš potřebovat poradit, podívej se do <a href="https://knihovna.utb.cz/knihovna/poprve-v-knihovne/manualy-a-navody/">průvodce knihovnou</a>, případně se obrať na pracovníky knihovny, kteří ti ochotně se vším poradí.&nbsp;</p><p>&nbsp;</p><p>Knihovna má své vlastní<strong> Nakladatelství UTB</strong>, díky němuž se<strong>&nbsp;</strong>nemusíš bát, že nebudeš mít kde vytisknout a svázat svou ročníkovku, bakalářku nebo diplomku. Výhodně si tam můžeš vytisknout i studijní materiály. Nakladatelství nabízí rychlé, levné a spolehlivé služby. Více najdeš na webu: <a href="https://nakladatelstvi.utb.cz/">nakladatelstvi.utb.cz</a>.&nbsp;</p>', 6, 1, '2023-09-22 13:15:33', '2023-09-22 13:16:47', 'knihovna-utb', '#acf0f9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (140, 'Koleje a menza', '<p>Studentům na UTB ve Zlíně slouží dvě menzy, bufet a restaurace, což je podle nás dost na to, abys zahnal svůj hlad mezi výukou. Každý den si vybereš z&nbsp;několika hotových&nbsp; jídel a minutek, a obvykle jsou v&nbsp;nabídce i 1-2 čerstvě upečené pizzy.&nbsp;</p><p>&nbsp;</p><p>Menza myslí i na vegetariány, pro které je vždy připraveno alespoň jedno bezmasé jídlo. Kromě toho si &nbsp;můžeš dát i různé druhy salátů, kompotů nebo dezertů.</p><p>&nbsp;</p><p>Vysokoškolské koleje se nacházejí poblíž centra města a ubytovat se můžeš v jednolůžkovém, dvoulůžkovém nebo třílůžkovém pokoji. &nbsp;Všechny koleje jsou rekonstruované a úroveň ubytování v&nbsp;nich je vysoká. Na těch nejnověji opravených najdeš i posilovnu a studentský klub. Více informací o kolejích najdeš na <a href="https://kmz.utb.cz">https://kmz.utb.cz</a></p>', 6, 3, '2023-09-22 13:18:12', '2023-09-22 13:18:12', 'koleje-a-menza', '#acf0f9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (141, 'IS/STAG', '<p>Studijní informační systém IS/STAG&nbsp; je nejdůležitější webovou aplikací, se kterou budeš během studia pracovat. Najdeš zde téměř vše, co se bude týkat tvého studia. Od povinností, které máš splnit, přes rozvrh, až např. po databázi závěrečných vysokoškolských prací. Můžeš-li, projdi si jej hned na začátku, ušetříš si později mnoho času.</p><p>&nbsp;</p><p>Ve STAGu můžeš pracovat v pasivním režimu, tj. bez zadání svých přihlašovacích údajů. Nebude ti tak sice dovoleno měnit data v systému, ale můžeš si procházet strukturu studijních programů,&nbsp; hledat podrobnější informace o předmětech, procházet pracoviště fakult, rozvrhy, ale i třeba prohlížet již obhájené kvalifikační práce.</p><p>&nbsp;</p><p>Aktivní přístup získáš zadáním svého jména a hesla, a dostaneš se tak k nejdůležitější záložce Moje studium. Budeš tak moci kontrolovat svou studijní úspěšnost, své povinnosti, zažádat si o ubytovací nebo sociální stipendium, hlásit se na zkoušky nebo procházet svůj rozvrh.</p><p>Po každé návštěvě se hlavně nezapomeň odhlásit. Zabráníš tak tomu, aby se někdo dostal k informacím o tvém studiu, měnil tvé údaje za tvými zády, zvláště na počítačích v knihovně.</p><p>&nbsp;</p><p>Vyskytne-li se technický problém s chodem STAGu, napiš na helpdesk <a href="mailto:stag@utb.cz">stag@utb.cz</a> nebo na <a href="mailto:portal@utb.cz">portal@utb.cz</a>. Do zprávy uveď své osobní číslo (např. A10123), nikdy neuváděj své rodné číslo.</p>', 1, 3, '2023-09-22 13:26:38', '2023-09-22 14:20:43', 'is-stag', '#ffdec9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (142, 'Moje UTB', '<p>Aplikace Moje UTB je oficiální mobilní aplikace Univerzity Tomáše Bati ve Zlíně. Najdeš v&nbsp;ní detailní přehled o studiu včetně rozvrhu, harmonogramu zkouškových termínů nebo interaktivní mapy kampusu. Můžeš si zapisovat či odepisovat zkouškové termíny a mít tak plnou kontrolu nad svým studiem. A co víc, jsi ihned upozorněn na známku zadanou v IS/STAG nebo na uvolnění zaplněného zkouškového termínu.</p><p>&nbsp;</p><p>Odkazy ke stažení:<br><a href="https://apps.apple.com/cz/app/moje-utb/id1598369807?l=cs">iOS</a></p><p><a href="https://play.google.com/store/apps/details?id=cz.unizone.utb&amp;hl=cs&amp;gl=US">Android</a></p>', 1, 3, '2023-09-22 13:29:39', '2023-09-22 13:29:39', 'moje-utb', '#ffdec9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (143, 'Hodnocení kvality výuky', '<p>Hodnocení probíhá přes IS/STAG, kde hodnotíš svou výuku v&nbsp;daném semestru a její jednotlivé předměty. Můžeš připsat anonymní nebo podepsaný komentář, obecnou připomínku k výuce či návrh na změnu. Pokud jsi s&nbsp;výukou spokojený/á, rozhodně to napiš také. Ocenění výuky a práce pedagoga je pro něj tou nejlepší zpětnou vazbou!&nbsp;</p><p>Hodnocení je naprosto anonymní, a pokud jsi kvalitu výuky ohodnotil/a, můžeš vyhrát nějakou cenu nebo třeba získat možnost dřívějšího předzápisu, či dostat jinou výhodu, kterou tvá fakulta může nabízet.&nbsp;</p><p>&nbsp;</p><p>Dej si pozor při odesílání: jakmile hodnocení odešleš, už se k němu nebudeš moci vrátit.</p><p>&nbsp;</p><p>Připomínky a hodnocení vidí jen kompetentní pracovníci a garanti dotčených předmětů, kteří s&nbsp;nimi &nbsp;dále pracují, vyhodnocují je a přijímají následná opatření. Výsledky hodnocení najdeš na webu své fakulty. Na setkání zástupců vedení fakulty a ústavů se studenty se pak dozvíš, jaká byla přijata opatření. Můžeš tam také diskutovat o všem, co tě ohledně studia zajímá nebo trápí.&nbsp;</p><p>&nbsp;</p><p>Rozhodně to není zbytečná věc. Aby hodnocení skutečně přispívalo ke zlepšování kvality výuky, je nutné, aby se do něj zapojilo co nejvíce studentů. Výsledky hodnocení jsou nezbytné také pro různé hodnotící zprávy studijních programů, fakulty a univerzity. Zpětná vazba ze strany studentů je zkrátka pro univerzitu důležitá. &nbsp;</p>', 1, 3, '2023-09-22 14:17:11', '2023-09-22 14:17:11', 'hodnoceni-kvality-vuky', '#ffdec9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (144, 'ISKAM', '<p>Pod zkratkou ISKAM se skrývá Informační systém kolejí a menz. Jde o online aplikaci, v&nbsp;níž si můžeš podat žádost o ubytování, zaplatit kauci nebo si hlídat stav svého konta a měsíční platby kolejného.</p><p>&nbsp;</p><p>Web ISKAM najdeš <a href="https://iskam.utb.cz/">zde</a>.</p>', 1, 1, '2023-09-22 14:19:02', '2023-09-22 14:19:02', 'iskam', '#ffdec9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (145, 'Moodle', '<p>Moodle je velmi oblíbený softwarový produkt pro tvorbu a realizaci výukových systémů a elektronických kurzů. Najdeš v něm studijní materiály, přednášky, zadané úkoly apod. Umí také automaticky vyhodnocovat testy a odevzdané práce, ankety a další důležité věci. Pro tvé studium rozhodně nutná věc.</p><p>&nbsp;</p><p>Pokud by ti v&nbsp;Moodle bylo cokoliv nejasného, mrkni do přehledného manuálu, který ti odpoví na veškeré dotazy.&nbsp;</p><p>&nbsp;</p><p><a href="https://www.utb.cz/?mdocs-file=59463">Studentský manuál Moodle</a></p>', 1, 3, '2023-09-22 14:19:27', '2023-09-22 14:19:27', 'moodle', '#ffdec9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (146, 'Psychologická poradna', '<p>Potřebuješ pomoc nebo radu při řešení problémů spojených se studiem či při řešení závažných životních situací a rozhodnutí? Zajímáš se o vlastní osobní rozvoj a profesní kariéru nebo hledáš odpovědi na otázky osobního a vztahového charakteru? Pak kontaktuj Psychologickou poradnu a sjednej si konzultaci.&nbsp;</p><p>&nbsp;</p><p>Nemusíš mít z ničeho obavy, psycholog vždy respektuje tvé soukromí a zachovává mlčenlivost.</p><p>Případně můžeš využít i internetovou poradnu nebo se domluvit na on-line konzultaci.</p><p>&nbsp;</p><p>Více informací nalezneš <a href="https://akademickaporadna.utb.cz/poradna/">zde</a>.</p>', 7, 3, '2023-09-22 14:22:20', '2023-09-22 14:34:09', 'psychologicka-poradna', '#bdf7b4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (147, 'Právní poradna', '<p>Na právní poradnu se můžeš obrátit, pokud potřebuješ poradit v pracovněprávní či občanskoprávní záležitosti, nebo chceš s&nbsp;odborníky konzultovat rodinné právo v souvislosti se skloubením rodinného a pracovního života.&nbsp;</p><p>Poradna poskytne právní podporu také těm, kdo zvažují zahájení podnikání během studia, anebo mají nejasnosti ohledně ochrany duševního vlastnictví.&nbsp;</p><p>&nbsp;</p><p>Detailnější informace o poradně nalezneš <a href="https://akademickaporadna.utb.cz/pravni-poradna/">zde</a>.</p>', 7, 1, '2023-09-22 14:30:41', '2023-09-22 14:30:41', 'pravni-poradna', '#bdf7b4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (148, 'Sociální poradna', '<p>Ocitl ses v obtížné životní situaci nebo tě tíží finanční problémy? Pomocnou ruku ti podá Sociální poradna.</p><p>&nbsp;</p><p>Sociální poradnu můžeš kontaktovat na tomto <a href="https://akademickaporadna.utb.cz/stipendia/">odkaze.</a></p>', 7, 1, '2023-09-22 14:32:32', '2023-09-22 14:34:18', 'socialni-poradna', '#bdf7b4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (149, 'Centrum pro studenty se specifickými potřebami', '<p>Jsi studentem se specifickými potřebami, potýkáš se se zrakovým, sluchovým či pohybovým postižením, dyslexií, autismem či psychickými obtížemi? Můžeme ti nabídnout služby, které vytváří rovné vzdělávací podmínky pro všechny studenty bez výjimky a pomohou ti při studiu.</p><p>&nbsp;</p><p>Mezi servisní opatření patří např.:</p><ul><li>časová kompenzace, asistenční služby, zpracovávání studijní literatury,</li><li>individuální studovna vybavená speciální technikou,</li><li>podpora akademického psaní a pomoc v oblasti cizích jazyků,</li><li>podpora studijního pobytu v zahraničí.</li></ul><p>&nbsp;</p><p>Více najdeš na následujícím <a href="https://akademickaporadna.utb.cz/centrum/">odkaze</a>.</p>', 7, 1, '2023-09-22 14:33:50', '2023-09-22 14:33:50', 'centrum-pro-studenty-se-specifickmi-potebami', '#bdf7b4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (150, 'Pedagogicko-psychologicko-právní poradna na FLKŘ', '<p>Také na FLKŘ v&nbsp;Uherském Hradišti se můžeš obrátit na poradnu, jejíž pracovníci ti poskytnou rady, týkající se problémů se studiem. Pomohou ti také s osobním rozvojem, se zvládáním stresu,&nbsp; posilováním sebedůvěry nebo v&nbsp;případě, že potřebuješ poradit v&nbsp;oblasti podnikání.&nbsp;</p><p>&nbsp;</p><p>Detailnější informace o poradně najdeš na tomto <a href="https://flkr.utb.cz/studium/moznosti-studia/ppp-poradna/">odkaze</a>.</p>', 7, 2, '2023-09-22 14:36:42', '2023-09-22 14:36:42', 'poradna-flkr', '#bdf7b4', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (151, 'Studentská unie UTB', '<p>Možná jsi o nich už slyšel/a. Jsou to ti, co organizují velké akce jako jsou <strong>Reprezentační ples univerzity,</strong> <strong>Majáles</strong> nebo <strong>Vítání prváků</strong>. Ti, co se starají o to, aby byl tvůj studentský život nezapomenutelný. Ti, co přemluvili menzu, aby pekla vlastní pizzu. A mnoho dalšího. Pokud se chceš jen účastnit akcí pořádaných unií nebo by ses rád do této party aktivně zapojil, &nbsp;sleduj sociální sítě Studentské unie UTB, ať ti nic neujde!&nbsp;</p><p>&nbsp;</p><p><a href="https://www.instagram.com/studentskaunieutb/">Instagram</a></p><p><a href="https://www.facebook.com/studentskaunieutb">Facebook</a></p>', 3, 1, '2023-09-22 14:39:43', '2023-09-22 14:39:43', 'studentska-unie-utb', '#ffe793', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (152, 'Unie studentů v Uherském Hradišti', '<p>Vysoká škola, to přece nejsou jen přednášky, učení a stres. Proto i v&nbsp;Uherském Hradišti působí tým studentů, kteří se snaží sobě i svým spolužákům studium a volný čas mimo něj zpříjemnit. Členové týmu mají rádi Uherské Hradiště a milují svoji Alma Mater, kterou je Fakulta logistiky a krizového řízení UTB, která sídlí v&nbsp;tomto krásném městě. Užij si ho s&nbsp;nimi!</p><p>&nbsp;</p><p><a href="https://www.instagram.com/uniestudentuuh/">Instagram</a></p><p><a href="https://www.facebook.com/UnieStudentuUH">Facebook</a></p>', 3, 2, '2023-09-22 14:45:56', '2023-09-22 14:45:56', 'usuh', '#ffe793', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (153, 'AIESEC', '<p>Ve Zlíně sice nemá svou pobočku, ale je dobré o něm vědět. AIESEC (Association Internationale des Étudiants en Sciences Économiques et Commerciales, t. j. Mezinárodní sdružení studentů vysokých škol ekonomických a obchodních)<strong>&nbsp;</strong>je největší studentská organizace na světě, která přispívá k rozvoji mladých lidí a nabízí jim příležitosti v mezinárodním prostředí. U AISECU můžeš využít své teoretické znalosti ze studia v oblastech obchodu, marketingu, managementu, financí, event managementu a cizích jazyků. Pokud hledáš nové příležitosti k rozvoji a k&nbsp;nastartování kariéry, je AIESEC pro tebe to pravé.&nbsp;</p><p>&nbsp;</p><p><a href="http://www.aiesec.cz">www.aiesec.cz</a></p>', 3, 3, '2023-09-22 14:46:38', '2023-09-22 14:46:38', 'aiesec', '#ffe793', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (154, 'ESN', '<p>Nemusíš cestovat za cizími kulturami, ony přijdou za tebou samy. Organizace ESN (Erasmus Student Network) existuje na školách a univerzitách po celém světě a zlínská pobočka je její součástí. Výměnným studentům (rozuměj Erasmákům, neboli zahraničním studentům, kteří nějakou část svého studia tráví na UTB) ulehčují studenti této pobočky život ve Zlíně, pomáhají jim zorientovat se v&nbsp;novém prostředí, organizují pro ně výlety za poznáním Česka a mnoho dalších akcí. Chceš se také zdokonalit v&nbsp;cizích jazycích a získat přátele z&nbsp;mnoha zemí světa? Napiš na <a href="mailto:hr@buddysystem.utb.cz">hr@buddysystem.utb.cz</a> a přidej se k&nbsp;ESN.</p><p>&nbsp;</p><p><a href="https://esnzlin.utb.cz/">ESN Zlín</a></p>', 3, 1, '2023-09-22 14:48:17', '2023-09-22 14:48:17', 'esn', '#ffe793', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (155, 'IAESTE', '<p>IAESTE (The International Association for the Exchange of Students for Technical Experience)&nbsp;je mezinárodní nevládní a nezisková organizace, která poskytuje studentům technických a dalších inženýrských oborů příležitost získat placené pracovní zkušenosti. Působí ve více než 80 zemích světa, v&nbsp;nichž organizuje výměnné programy a propojuje firmy a instituce se stážisty.&nbsp;</p><p>&nbsp;</p><p><a href="https://www.iaeste-zlin.org/">IAESTE</a></p>', 3, 3, '2023-09-22 14:54:28', '2023-09-22 14:54:28', 'iaeste', '#ffe793', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (156, 'UTB za klima', '<p>Studentský spolek UTB za klima sdružuje studenty, kterým jsou blízká témata jako ekologie, udržitelnost či budoucnost. Spolek chce podporovat environmentálně příznivé chování každého jedince na univerzitě, podporovat rozvoj univerzitního prostředí v&nbsp;souladu s&nbsp;pravidly udržitelného rozvoje či zvyšovat povědomí veřejnosti o stavu klimatu. Činnost spolku vychází z&nbsp;iniciativy Univerzity za klima.&nbsp;</p><p>&nbsp;</p><p><a href="https://univerzityzaklima.cz/">www. univerzityzaklima.cz</a></p>', 3, 3, '2023-09-22 14:55:03', '2023-09-22 14:55:03', 'utb-za-klima', '#ffe793', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (157, 'WiFi', '<p>Ve všech univerzitních budovách se můžeš připojit k&nbsp;síti Eduroam. Pro aktivaci svého účtu v&nbsp;této síti je nutné se při prvním připojení z&nbsp;nového zařízení přihlásit svým univerzitním účtem.</p>', 5, 3, '2023-09-22 15:58:42', '2023-09-22 15:58:42', 'wifi', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (158, 'Průkaz studenta', '<p>Bez kartičky Průkaz studenta se na univerzitě neobejdeš: otevře ti brány univerzitních budov, učeben i knihovny, umožní ti zaplatit za oběd v menze nebo za tisk na tiskárnách, které míjíš na chodbách.</p><p>&nbsp;</p><p>Průkaz je platný po celou dobu studia, jeho vydání je zdarma, ale je důležité poznamenat, že průkaz opravdu nechceš ztratit, věř nám! Je to tvoje vysokoškolská občanka.&nbsp;</p><p>&nbsp;</p><p>Případnou ztrátu studijního průkazu musíš nahlásit na Centrum výpočetní techniky (CVT) a nechat si vyrobit průkaz nový, za poplatek 1000 Kč. Pokud se jedná o krádež, je třeba ji ohlásit na Policii ČR a nechat si vystavit doklad. S tímto dokladem tě bude stát výroba nového průkazu jen 200 Kč.</p>', 5, 3, '2023-09-22 15:59:39', '2023-09-22 15:59:39', 'prukaz-studenta', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (159, 'Oslovování vyučujících', '<p>Akademičtí a výzkumní pracovníci mají obvykle více než jeden titul. Oslovovat je správným titulem je důležitější, než by se mohlo na první pohled zdát. Je jasné, že se v tom hned nevyznáš, a proto ti trochu napovíme:</p><ul><li>akademická funkce má přednost před titulem („pane rektore“, „pane děkane“, „paní proděkanko“ atd.),</li><li>pokud má akademik více titulů, oslovujeme jej tím nejvýznamnějším, a to v pořadí: profesor, docent, doktor, magistr/inženýr,</li><li>tituly Bc. (bakalář) a CSc. (kandidát věd) se neoslovuje.</li></ul>', 5, 3, '2023-09-22 16:00:20', '2023-09-22 16:00:20', 'oslovovani-vyuujicich', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (160, 'Psaní emailu', '<p>E-mailovou adresu svého adresáta najdeš vždy mezi kontakty.utb.cz nebo v IS/STAG. Při komunikaci se školou je nutné používat vždy tvůj vygenerovaný školní e-mail @utb.cz (přihlášení najdeš na outlook.utb.cz – pro přihlášení použij svůj univerzitní login).</p><p>&nbsp;</p><p>Důležitým prvkem každého e-mailu je předmět! Pokud jej nevyplníš, může adresátovi e-mail přijít do spamu, a neodpoví ti nikdy.</p><p>&nbsp;</p><p>Vyučujícího v&nbsp;e-mailu oslovuj vždy titulem. Např.. „Vážený pane profesore/pane docente,“ nebo „Vážená paní profesorko/paní docentko,“ apod.</p><p>&nbsp;</p><p>Ve&nbsp;zprávě samotné popiš, o co žádáš. Platí zásada: „čím stručněji, tím lépe“. Pokud to nejde, můžeš na konci zprávy shrnout to nejpodstatnější.</p><p>&nbsp;</p><p>Rozloučení lze formulovat několika způsoby, např. „Těším se na Vaši odpověď.“ nebo „Děkuji Vám předem za odpověď.“ Pokud se domlouváte na osobním setkání, můžeš napsat např. „Budu se těšit na setkání s Vámi.“</p><p>&nbsp;</p><p>Poslední, ale také podstatný prvek. Napiš v závěru své jméno, příjmení a označení studijního programu, příp. označení studijní skupiny a své číslo studenta.&nbsp;</p><p>&nbsp;</p><p>Dodržuj vlákno konverzace – nevytvářej nový e-mail s novým předmětem, pokud to není nutné.</p>', 5, 3, '2023-09-22 16:01:48', '2023-09-22 16:02:36', 'psani-emailu', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (161, 'Tisk, kopírování a skenování', '<p>Jako student můžeš využívat tiskárny, které jsou umístěny ve všech budovách, ve společných prostorech. Co pro to musíš udělat?</p><p>&nbsp;</p><ul><li>musíš mít dobitý kredit na své studentské kartě,</li><li>přihlásíš se na některý ze studentských počítačů,</li><li>použiješ přihlašovací údaje (uživatelské jméno a heslo) stejné jako do IS/STAG.</li></ul><p>&nbsp;</p><p>Samotná tiskárna se spustí načtením tvé studentské karty (čtečka je na pravém boku tiskárny).</p><p>Je to složité? Tak ti dáme ještě jednu možnost. Možná je ještě trochu složitější, ale když ji zmákneš, můžeš si tisknout ze svého počítače, notebooku či mobilu. Lidově se tomu říká SafeQTisk.</p><p>&nbsp;</p><p>Tentokrát postupuj následovně:&nbsp;</p><ul><li>v prohlížeči zadej adresu https://printsrv.fmk.utb.cz/safeq</li><li>přihlas se pod svým uživatelským jménem a heslem, jako do IS/STAG,&nbsp;</li><li>vlož soubor, který chceš vytisknout a vyber možnost tisku (ČB tisk, jedno/oboustranný),</li><li>přijď k tebou zvolené tiskárně, která je napojená na systém SafeQTisk, přilož kartičku a tiskni.</li></ul><p>&nbsp;</p><p>Určitě se ptáš “A kolik mě to bude stát?”.&nbsp;</p><p>&nbsp;</p><p>Ceny jsou zde:&nbsp;</p><ul><li>černobílá kopie/tisk A4: 1,20 Kč</li><li>černobílá kopie/tisk A3: 2,40 Kč&nbsp;</li><li>barevná kopie/tisk A4: 5,80 Kč&nbsp;</li><li>barevná kopie/tisk A3: 11,60 Kč&nbsp;</li><li>skenování: zdarma.</li></ul>', 5, 3, '2023-09-22 16:03:33', '2023-09-22 16:07:16', 'tisk-kopirovani-a-skenovani', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (162, 'Dobíjení studentské karty', '<p>Pro placení obědů v&nbsp;menze, svačin v&nbsp;bufetu nebo i kopírování na univerzitě potřebuješ mít nabitou studentskou kartu.</p><p>Máš dvě možnosti, jak svou studentskou kartu dobít. Pokud si chceš kartu dobít fyzicky, navštiv v&nbsp;otevírací době menzu, ať už v&nbsp;budově U4 nebo U5, restauraci v&nbsp;budově U13 nebo bufet na U2, kde si můžeš kartu na pokladně dobít s&nbsp;poplatkem 30 Kč. Druhou variantou je bezplatné online dobití po přihlášení, prostřednictvím portálu <a href="https://jidelnicek.utb.cz/webkredit/Account/Payments">WebKredit</a>.</p>', 5, 3, '2023-09-22 16:08:16', '2023-09-22 16:08:16', 'dobijeni-studentske-karty', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (163, 'Výjezdy do zahraničí', '<p>Láká tě představa, že bys mohl/a část svého studia strávit v&nbsp;zahraničí? Poznat jinou zemi, získat nové kamarády a zdokonalit své dovednosti a kompetence? Pak je semestrální či celoroční výjezd tou správnou volbou. Můžeš dokonce získat stipendium, které ti pokryje největší náklady spojené se zahraničním studiem či pracovní stáží.&nbsp;</p><p>Informuj se na webu <a href="https://www.utb.cz/mobilita">utb.cz/mobilita</a> nebo na Mezinárodním oddělení UTB.&nbsp;</p>', 5, 3, '2023-09-22 16:08:53', '2023-09-22 16:08:53', 'vyjezdy-do-zahranici', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (164, 'Job centrum UTB', '<p>Pokud chceš pomoci s hledáním práce, brigády, pracovní stáže nebo třeba jen s úpravou životopisu, pak se můžeš obrátit na Job centrum UTB, které sídlí na rektorátu, v&nbsp;budově &nbsp;U13.</p><p>&nbsp;</p><p>Můžeš zkusit také koučování, studentskou stáž přímo na UTB nebo přijít na jeden z workshopů, kde si zdokonalíš některou ze svých dovedností. Všechno je zdarma.&nbsp;</p><p>Nečekej, až ti ty nejlepší brigády a stáže vyfouknou kolegové!&nbsp;</p><p>&nbsp;</p><p>Více najdeš na webu:</p><p><a href="https://jobcentrum.utb.cz/">jobcentrum.utb.cz</a></p>', 5, 3, '2023-09-22 16:09:31', '2023-09-22 16:09:31', 'job-centrum-utb', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (165, 'Vysokoškolský sportovní klub', '<p>Otevři si cestu ke sportování za zvýhodněných podmínek! Jestli máš chuť dělat sport na výkonnostní nebo vrcholové úrovni a chceš reprezentovat naši univerzitu na Českých univerzitních hrách nebo v&nbsp;dlouhodobých soutěžích a k tomu ještě dostávat hezké sportovní stipendium, staň se členem Vysokoškolského sportovního klubu. Členství ti také zpřístupní posilovnu a bazén ve vyhrazeném čase. Členský poplatek pro studenty UTB činí 1500 Kč na rok. V&nbsp;případě zájmu se obrať na <a href="https://fame.utb.cz/o-fakulte/zakladni-informace/struktura/ustavy/ustav-telesne-vychovy/kontakty/">Ústav tělesné výchovy</a>.&nbsp;</p>', 5, 3, '2023-09-22 16:10:05', '2023-09-22 16:10:05', 'vysokokolsk-sportovni-klub', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (166, 'Maths Support Centre', '<p>Potřebuješ pomoc s dohnáním, zopakováním a pochopením matematiky? Pak je tu pro tebe Maths Support Centre, v&nbsp;němž najdeš podporu bez ohledu na tvou úroveň v matematice. Můžeš sem docházet pravidelně nebo jednotlivě, bez předchozího ohlášení, a veškeré služby jsou pro tebe zdarma.</p><p>&nbsp;</p><p>Centrum najdeš na Fakultě aplikované informatiky, v&nbsp;budově U5. Pro podrobnější informace jdi na <a href="https://fai.utb.cz/msc/">web</a> centra.</p>', 5, 1, '2023-09-22 16:10:44', '2023-09-22 16:10:44', 'maths-support-centre', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (167, 'Studentská účetní a daňová kancelář', '<p>Jako student můžeš na Fakultě managementu a ekonomiky využít Studentskou účetní a daňovou kancelář, kde ti poradí, jak prakticky zpracovat účetnictví, daňovou evidenci, mzdy nebo daňová přiznání fyzických a právnických osob přímo na reálných problémech klientů kanceláře. Studentská kancelář nabízí pomoc při řešení účetních a daňových případů studentům všech fakult, zaměstnancům UTB i veřejnosti.</p><p>&nbsp;</p><p>V&nbsp;případě zájmu se obrať na Ústav financí a účetnictví prostřednictvím emailu <a href="mailto:ufu@fame.utb.cz">ufu@fame.utb.cz</a> nebo navštiv kancelář 402 v&nbsp;budově U2.</p>', 5, 1, '2023-09-22 16:11:38', '2023-09-22 16:11:38', 'studentska-ucetni-a-danova-kancelar', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (168, 'Vizuál UTB', '<p>Tvoříš prezentaci, leták, plakát nebo potřebuješ v&nbsp;nějakém svém materiálu použít loga a&nbsp; barvy univerzity nebo fakulty? Pak musíš dodržet oficiální vizuální styl univerzity. Pro tyto účely je vytvořen web <a href="https://vizual.utb.cz/index.php">vizual.utb.cz</a>. Určitě se na něj nejdřív podívej, nebo na něj odkaž grafika, se kterým spolupracuješ. V&nbsp;případě pochybností se zeptej na Odboru marketingu a komunikace.&nbsp;</p>', 5, 3, '2023-09-22 16:12:19', '2023-09-28 11:54:50', 'vizual-utb', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (169, 'Oblečení a reklamní předměty', '<p>Reprezentovat univerzitu můžeš vědomostmi, sportem či talentem. Anebo jen vyraz do ulic v univerzitním oblečení a dej všem vědět, za jaký tým kopeš. Oblečení inspirované americkým stylem si oblíbí každý. Čekají tu na tebe klasické i cropped mikiny, baseballové bundy, bombery, trička nebo kulichy.</p><p>&nbsp;</p><p>Navštiv oficiální <a href="https://utbcz-my.sharepoint.com/personal/m1_kozel_utb_cz/Documents/be.utb.cz">showroom</a>, kde kromě stylového oblečení najdeš i reklamní předměty jako jsou tašky, tužky a různé drobnosti, které oceníš třeba při přípravě na zkoušky.&nbsp;</p><p>Na webu showroomu najdeš i otevírací dobu prodejny a kontaktní informace.</p>', 5, 3, '2023-09-22 16:13:02', '2023-09-22 16:13:02', 'merchendise', '#ffe4f3', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (170, 'Výzkumná pracoviště UTB', '<p><strong>Centrum polymerních systémů</strong></p><p>Centrum polymerních systémů je výzkumné pracoviště aktivně působící ve výzkumu a vývoji v oblasti zpracovatelství plastů a pryží, materiálového inženýrství a dalších chemicko-technologických oborech.</p><p><a href="https://cps.utb.cz/">cps.utb.cz</a>&nbsp;</p><p>&nbsp;</p><p><strong>Regionální výzkumné centrum CEBIA-Tech</strong></p><p>Centrum bezpečnostních, informačních a pokročilých technologií je organizační jednotka Fakulty aplikované informatiky zaměřená zejména na realizaci výzkumných aktivit. Zahrnuje mimo jiné vysoce specializované laboratoře ve Vědeckotechnickém parku, informační a komunikační technologie orientované na problematiku elektromagnetické kompatibility, technického vybavení budov a pokročilých metod materiálového inženýrství. V rámci fakulty je centrum CEBIA-Tech organizačně zařazeno na úrovni ústavů a na jeho chodu se podílejí vědecko-pedagogičtí a výzkumní pracovníci Fakulty aplikované informatiky.</p><p>Více najdeš na webu:<br><a href="https://fai.utb.cz/cebia-tech/">fai.utb.cz/cebia-tech</a></p>', 6, 3, '2023-09-22 16:14:53', '2023-09-22 16:14:53', 'vyzkumna-pracoviste', '#acf0f9', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (171, 'Akademický rok', '<p>Každý akademický rok začíná zimním semestrem. Před samotným zahájením výuky je doba zápisů a předzápisů, kdy se v&nbsp;určitém časovém předstihu přihlásíš na předměty budoucího semestru daného akademického roku prostřednictvím systému IS/STAG.</p><p>&nbsp;</p><p>Výuka v zimním semestru trvá 14 týdnů. Poslední týden výuky je zpravidla zápočtový. To je doba, kdy se plní různé povinnosti potřebné pro úspěšné splnění předmětu - píší se testy, kontrolují protokoly, odevzdávají seminárky a vše další, co po tobě vyučující požadují. Po tomto období následují vánoční prázdniny.</p><p>&nbsp;</p><p>V lednu pak začíná řádné zkouškové období. To trvá 5 týdnů, kdy se snažíš zdolat všechny zkoušky. Když se ti něco nepovede, máš možnost uspět ještě v opravném zkouškovém období, které navazuje na to řádné.</p><p>&nbsp;</p><p>Letní semestr začíná v únoru a výuka trvá 14 týdnů. Pokud jsi v končícím ročníku (3. ročník bakalářského studia nebo 2. ročník navazujícího magisterského studia), trvá výuka 10 týdnů. Stejně jako &nbsp;po zimním semestru, i po tom letním nastává zkouškové období: řádné 5-týdenní, u končících ročníků kratší 3-týdenní, a po něm opravné.&nbsp;</p><p>&nbsp;</p><p>Detailní harmonogram akademického roku, spolu s řadou dalších důležitých termínů, najdeš na webu své fakulty.</p><p>&nbsp;</p><p><a href="https://ft.utb.cz/studium/studijni-oddeleni-2/harmonogram-akademickeho-roku/">Akademický rok FT</a></p><p><a href="https://fmk.utb.cz/studium/studijni-oddeleni/harmonogram-akademickeho-roku/">Akademický rok FMK</a></p><p><a href="https://fhs.utb.cz/o-fakulte/uredni-deska/harmonogram-akademickeho-roku/">Akademický rok FHS</a></p><p><a href="https://fame.utb.cz/o-fakulte/uredni-deska/harmonogram-akademickeho-roku/harmonogram-akademickeho-roku-2023-2024/">Akademický rok FaME</a></p><p><a href="https://fai.utb.cz/o-fakulte/uredni-deska/harmonogram-akademickeho-roku/">Akademický rok FAI</a></p><p><a href="https://flkr.utb.cz/studium/studijni-oddeleni/harmonogram-akademickeho-roku/">Akademický rok FLKŘ</a></p>', 8, 3, '2023-09-28 11:40:08', '2023-09-28 11:40:08', 'akademick-rok', '#f7567c', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (172, 'Předměty', '<p>Jaké budeš studovat předměty a co vše budeš muset za semestr splnit? Všechno najdeš ve studijním plánu svého studijního programu či oboru. Je to tabulka, ve které jsou uvedeny předměty, které jsou pro tvůj program či obor povinné, povinně volitelné nebo volitelné. Dozvíš se z&nbsp;ní i kolik hodin týdně se budeš předmětu ve škole věnovat, jestli bude mít formu přednášky, semináře, cvičení nebo laboratoří, a vyčteš z ní i garanta předmětu (vyučujícího, který se stará o to, aby měl předmět vhodnou náplň).</p><p>&nbsp;</p><p>Pár vysvětlivek:</p><p>KÓD PŘEDMĚTU - každý předmět má svůj kód/zkratku, která vypadá třeba takto: TUIP/TP1LT. První část kódu označuje ústav, který má předmět pod palcem, v tomto případě je to UIP (Ústav inženýrství polymerů), T označuje Fakultu technologickou. Z kódu předmětu ještě vyčteš, jestli je předmět určený prezenčním nebo kombinovaným studentům. TP1LT je předmět pro prezenční studenty. Kombiňáci by měli tento předmět schovaný pod kódem TK1LT.</p><p>&nbsp;</p><p>POČET KREDITŮ - v tabulce studijního plánu najdeš tuto důležitou informaci pod zkratkou PK. Pro konkrétní předmět je to počet kreditů, které získáš, když předmět úspěšně absolvuješ. Počet kreditů ti taky napoví, jak náročný předmět bude. Čím víc kreditů, tím víc času a úsilí předmět zabere.</p><p>&nbsp;</p><p>POVINNÉ PŘEDMĚTY - tyto předměty prostě absolvovat musíš, bez nich se neobejdeš, chceš-li postoupit dál.</p><p>&nbsp;</p><p>POVINNĚ VOLITELNÉ PŘEDMĚTY - v tabulce studijního plánu je to skupina předmětů, ze kterých máš povinnost si některé zvolit (kolik a podle jakého klíče se dočteš přímo ve studijním plánu pod seznamem povinně volitelných předmětů; &nbsp;je tam napsáno třeba “Z bloku povinně̌ volitelných předmětů zvolte jednu úroveň angličtiny.”).</p><p>&nbsp;</p><p>VOLITELNÉ PŘEDMĚTY - jestli si některý z těchto předmětů zapíšeš nebo ne, je opravdu čistě na tobě.</p><p>&nbsp;</p><p>VÝUKA PŘEDMĚTU - může mít formu PŘEDNÁŠKY, SEMINÁŘE, CVIČENÍ a LABORATORNÍHO CVIČENÍ. Ve studijním plánu to objevíš pod zkratkami P, S, C a L. Předmět pak může vypadat tak, že v tabulce u zkratek P, S, L najdeš čísla 2, 1, 2. To pak znamená, že tento předmět bude obnášet 2 hodiny přednášek, 1 hodinu semináře a 2 hodiny v laborkách TÝDNĚ. Studenti kombinované formy mají ve studijním plánu časový rozsah uvedený jako počet hodin za celý semestr - jejich výuka totiž probíhá v blocích.</p><p>&nbsp;</p><p>UKONČENÍ PŘEDMĚTU</p><p>V tabulce ve sloupci “Ukon.” najdeš zkratky Z, ZK, KL. Pojďme je rozklíčovat:</p><p>Z - ZÁPOČET - každý předmět klade na studenta nějaké požadavky. Zápočet vyjadřuje, jestli jsi podmínky splnil/a nebo ne.</p><p>KL - KLASIFIKOVANÝ ZÁPOČET - u klasifikovaného zápočtu se neřeší jen splnil/nesplnil/a, ale vyučující ti známkou řekne i to, jak jsi předmět splnil/a (takže určitě na výbornou, za A).</p><p>ZK - ZKOUŠKA - může být písemná, ústní nebo kombinovaná. Každopádně zkouška už je náročnější záležitost, má ověřit, že ses během semestru fakt něco naučil/a, pochopil/a nebojíš se to použít :-). Na termín zkoušky se hlásíš přes IS/STAG a výsledek zkoušky ti vyučující zase zapíše do STAGu. Ze zkoušky dostaneš známku (taky za A samozřejmě).</p>', 8, 3, '2023-09-28 11:41:39', '2023-09-28 11:41:39', 'predmety', '#f7567c', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (173, 'Stipendium', '<p>Jako student můžeš mít nárok na různé typy stipendií. Vnitřní normy pro konkrétní stipendia, s podrobnými informacemi, najdeš na webu své fakulty v sekci Studium &gt; Studijní oddělení &gt; <a href="https://ft.utb.cz/studium/moznosti-studia/stipendia/">Stipendia</a> .</p><p><br>Stručně shrnuto, existují následující typy stipendií:</p><p><strong>UBYTOVACÍ STIPENDIUM</strong></p><p>Může být přiznáno studentovi v prezenční formě studia, který studuje první bakalářský nebo navazující magisterský studijní program, nepřekročil standardní délku studia a nemá místo trvalého bydliště v okrese Zlín.</p><ul><li>Žádat o něj můžeš elektronicky přes IS/STAG (<a href="http://stag.utb.cz/">IS/STAG</a> ➜ Moje studium ➜ Moje údaje ➜ Ubytovací stipendium ➜ Podání žádosti).</li><li>Vyplácí se dvakrát ročně: za první období (1. 9. – 31. 12.) v&nbsp;prosinci, a druhá výplata (za období 1. 1. – 30. 6.) je v&nbsp;červnu.</li><li>&nbsp;</li></ul><p><strong>SOCIÁLNÍ STIPENDIUM</strong></p><p>Podmínky udělení tohoto stipendia jsou dané zákonem o vysokých školách. Pokud si myslíš, že bys na něj měl/a nárok, rozhodně kontaktuj své studijní oddělení.&nbsp;</p><p>&nbsp;</p><p><strong>PROSPĚCHOVÉ STIPENDIUM</strong></p><p>Prospěchové stipendium je pro ty nejšikovnější studenty, kteří mají skvělý studijní průměr za předchozí akademický rok. Výše stipendia podle studijních výsledků se stanovuje aktuálně pro každý&nbsp; nový akademický rok rozhodnutím děkana.</p><p>&nbsp;</p><p><strong>MIMOŘÁDNÁ STIPENDIA</strong></p><p>Mimořádné stipendium můžeš dostat třeba za to, že pomáháš s propagací fakulty, podílíš se na organizaci různých akcí, ale také za práci na výzkumných projektech, pracuješ jako studentská vědecká síla nebo jsi členem&nbsp;akademického senátu fakulty či univerzity, dále za vynikající studijní výsledky v&nbsp;průběhu studia, jako podporu studia na UTB, podporu studijních pobytů a pracovních stáží v&nbsp;zahraničí, nebo jako výpomoc v&nbsp;mimořádné sociální situaci. Když víš, že ti má na účtě přistát nějaké mimořádné stipendium, sleduj IS/STAG. Tam se dozvíš, jestli ti bylo stipendium zadáno, a taky můžeš urychlit proces výplaty tím, že se odkliknutím ve STAGu “vzdáš práva na odvolání”. Zní to trochu divně, ale je to věc, která vyplývá ze zákona, a znamená, že po zadání stipendia běží 30-ti denní odvolací lhůta.&nbsp;</p>', 8, 3, '2023-09-28 11:42:51', '2023-09-28 11:42:51', 'stipendium', '#f7567c', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (174, 'Přerušení studia nebo změna formy studia', '<p>O změnu formy studia můžeš požádat děkana příslušné fakulty na předepsaném formuláři prostřednictvím studijního oddělení. Změnou se však předchozí studium z hlediska poplatků za delší studium stává neúspěšným, zaznamenává se do centrální matriky a automaticky končí výplata ubytovacího stipendia. O změnu je třeba žádat před zahájením semestru.</p><p>&nbsp;</p><p>O přerušení studia je také třeba požádat písemně děkana příslušné fakulty. Podmínky pro přerušení studia najdeš ve <a href="https://www.utb.cz/?mdocs-file=42265">Studijním a zkušebním řádu UTB.&nbsp;</a></p><p>&nbsp;</p><p><a href="https://ft.utb.cz/o-fakulte/zakladni-informace/struktura/celofakultni-pracoviste/studijni-oddeleni/">Studijní oddělení FT</a><br><a href="https://fmk.utb.cz/o-fakulte/zakladni-informace/struktura/studijni-oddeleni/">Studijní oddělení FMK</a></p><p><a href="https://fame.utb.cz/o-fakulte/zakladni-informace/struktura/studium-a-celozivotni-vzdelavani/studijni-oddeleni/">Studijní oddělení FaME</a></p><p><a href="https://fai.utb.cz/studium/studijni-oddeleni/">Studijní oddělení FAI</a></p><p><a href="https://fhs.utb.cz/studium/studijni-oddeleni/uredni-hodiny/">Studijní oddělení FHS</a></p><p><a href="https://flkr.utb.cz/studium/studijni-oddeleni/">Studijní oddělení FLKŘ</a></p>', 8, 3, '2023-09-28 11:44:49', '2023-09-28 11:50:32', 'uprava-studia', '#f7567c', '', '', 1);
INSERT INTO web_pruvodcestudenta_be.topics (id, title, description, section_id, location_id, created_at, updated_at, slug, color, url, location, visible) VALUES (175, 'Zakládání nových studentských spolků', '<p>Chybí ti na UTB volnočasová aktivita, které by ses rád/a věnoval/a? Myslíš si, že bys dokázal/a studentský spolek vést? Na UTB k&nbsp;tomu máš ideální příležitost. Můžeš s&nbsp;kolegy založit vlastní spolek a univerzita vás ve vaší činnosti podpoří. Samozřejmě za předpokladu, že dodržíte potřebná pravidla.&nbsp;</p><p>&nbsp;</p><p><a href="https://www.utb.cz/studentske-spolky/?fbclid=PAAaZSnIdRexBVgAWWUV6nwun672X09qYlYG9AH3FTZQCnYznvpw0rggprw7U">Pravidla zakládání nových studentských spolků</a></p>', 3, 3, '2023-09-28 11:52:04', '2023-09-28 11:57:07', 'zakladani-novych-studentskych-spolku', '#ffe793', '', '', 1);
